def numsound(num_str):

	if len(num_str)>3:
		print('本程序只适合3位数以内的算术计算')
		exit()

	

	for x in range(0,len(num_str)):
		
		if len(num_str)==2 and num_str[0]=='1' and x==0:
			1==2 #排除16读成 一十六 的情况，正确的应该是  十六
		else:
			sound = AudioSegment.from_wav(whovoice + str(num_str[x])+ '.wav')
			play(sound)


		soundstr=''
		if len(num_str)==3 and x==0:
			soundstr = 'bai'

			if soundstr!='':
				sound = AudioSegment.from_wav(whovoice + soundstr+ '.wav')
				play(sound)

			if num_str[x+1:].replace('0','')=='':
				break




		soundstr=''
		if (len(num_str)==3 and x==1) or (len(num_str)==2 and x==0):
			soundstr = str('shi')
			
			if num_str[x]=='0':
				soundstr = ''

			if soundstr!='':
				sound = AudioSegment.from_wav(whovoice + soundstr+ '.wav')
				play(sound)

			if num_str[x+1:].replace('0','')=='':
				break


		


