def playsounds(question):

	question_array=question.replace('+','***').replace('-','***').replace('=','***').split('***')
	question_num=0

	

	

	for x in range(0,len(question_array)-1):


		numsound(str(question_array[x]))

		
		question_num=question_num+len(str(question_array[x]))
		
		soundstr=''
		if question[question_num]=='+':
			soundstr = str('jia')
		if question[question_num]=='-':
			soundstr = str('jian')
		if question[question_num]=='=':
			soundstr = str('dengyu')

		if soundstr!='':
			sound = AudioSegment.from_wav(whovoice + soundstr+ '.wav')
			play(sound)
		else:
			print('没找到音频文件：'+ whovoice + soundstr+ '.wav')
		question_num=question_num+1




