def question_make_plus(question_num):
	question_str=''
	for x1 in range(0,question_num-1):
		question_make_plus_i = random.randint(0,len(question_list)-1)
		if question_str =='':
			question_str = question_list[question_make_plus_i]
		else:
			random.shuffle(question_list)
			for x2 in range(0,len(question_list)-1):
				
				question_temp_array=question_list[x2].replace('+', '***').replace('-','***').split('***');
				#print('【'+str(x2)+'】:'+question_str+'   '+question_list[x2])

				if eval(question_str) == int(question_temp_array[0]):
					question_str = question_str + question_list[x2][len(question_temp_array[0]):]
					#print('+++：'+question_list[x2]+'   '+question_list[x2][len(question_temp_array[0]):])
					#print('+++：'+question_str)
					break
				#else:
					#print('【'+str(x2)+'】:'+'没找到'+question_str)
	return question_str


rightNum=0
errorNum=0
time_start=time.time()


while(True):
	question_str=question_make_plus(question_num)


	os.system('clear')
	print('第 '+str(rightNum+1)+' 题：')
	print(' ')
	print('\033[33m'+question_str+ ' = ')
	

	#随机选择声音库
	if random.randint(1,2)==1:
		whovoice='mollysound/' 
	else:
		whovoice='babasound/'

	playsounds(question_str + '=')

	os.system('clear')
	print('\033[33m第 '+str(rightNum+1)+' 题：')
	print(' ')
	message=input('\033[32m'+question_str+ ' = ')

	while message!=str(eval(question_str)):
		if(message=='q'):
			os.system('clear')
			time_end=time.time()

			print('总共做题：'+str(rightNum)+' 道')
			print('正确：'+str(rightNum)+' 道')
			print('错误：'+str(errorNum)+' 次')

			if rightNum!=0:
				m, s = divmod(time_end-time_start, 60)
				h, m = divmod(m, 60)
				print('总耗时：'+ "%02d:%02d:%02d" % (h, m, s))
				print('平均：'+ str(int((time_end-time_start)/rightNum))+' 秒/题')

			print('\033[0m')
			message=input('')
			if message=='q':
				exit()
			break
		else:
			if message=='':
				os.system('clear')
				message=input('\033[32m'+question_str+ ' = ')
			else:
				sound = AudioSegment.from_wav(whovoice + 'cuole.wav')
				play(sound)
				print('\033[31m')
				print('*' * 20)
				print('     错误！！！')
				print('*' * 20)
				print('\033[0m')
				message=input('\033[32m'+question_str+ ' = ')
				errorNum=errorNum+1

	else:
		os.system('clear')
		#print('\033[32m'+question_str+ ' = '+str(eval(question_str)))
		#print('正确！')
		rightNum=rightNum+1
		

		






